// Import React and Material UI components as before...
import { Card, CardContent, CardActions, Typography, IconButton } from '@mui/material';
import { Button, Grid } from '@mui/material';
import { StringDecoder } from 'string_decoder';
import { UserContextType } from './Types';
import { UserContext } from './Auth';
import { useNavigate } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import { CommunityType } from './Types';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import HeartIcon from '@mui/icons-material/Favorite';

const PostCard = ({ post, onLike, isLiked, likesCount }) => {
    // Like button click handler remains the same
    const handleLikeClick = () => {
      onLike(post._id);
    };

    return (
      <Card sx={{ flexDirection: 'column', minHeight: 100, width: "100%", backgroundColor:'#efc9e4' }}>
        <CardContent>
          <Typography variant="h6" gutterBottom align="left">
            {post.title}
          </Typography>
          <Typography variant="body1" color="text.secondary" align="left">
            {post.description}
          </Typography>
        </CardContent>
        <CardActions sx={{ marginTop: 'auto', display: 'flex', justifyContent: 'space-between' }}>
          {/* Comment and Like buttons as before */}
          <IconButton aria-label="like post" onClick={handleLikeClick}>
            <HeartIcon
              style={{
                fontSize: '16px',
                fill: isLiked ? 'red' : 'none',
                stroke: isLiked ? 'red' : 'black',
                strokeWidth: '2px',
              }}
            />
          </IconButton>
          {likesCount} {/* Display the likesCount passed as a prop */}
        </CardActions>
      </Card>
    );
  };

  export default PostCard;
