// Created by Georgina Alacaraz
import MuiBottomNavigation from "../MuiBottomNavigation";
import Header from "./components/Header";
import { Box, Button, Grid, Typography } from "@mui/material";
import { UserContextType, MyPaymentMetadata , CommunityType} from "./components/Types";
import { onCancel, onError, onReadyForServerApproval, onReadyForServerCompletion } from "./components/Payments";
import SignIn from "./components/SignIn";
import ProductCard from "./components/ProductCard";
import { UserContext } from "./components/Auth";
import React from "react";
import { useState, useEffect } from "react";



export default function HomePage() {
  const { user, saveUser, showModal, saveShowModal, onModalClose, community } = React.useContext(UserContext) as UserContextType;
  const [createCommunityData, setCreateCommunityData] = useState(null);


  const orderProduct = async (memo: string, amount: number, paymentMetadata: MyPaymentMetadata) => {
    if(user.uid === "") {
      return saveShowModal(true);
    }

    const paymentData = { amount, memo, metadata: paymentMetadata };
    const callbacks = {
      onReadyForServerApproval,
      onReadyForServerCompletion,
      onCancel,
      onError

    };

    const payment = await window.Pi.createPayment(paymentData, callbacks);
    console.log(payment);
  }
  useEffect(() => {
    // Make an API call to fetch the create community data
    const fetchData = async () => {
      const response = await fetch('/hi');
      const community = await response.json();
      console.log('Fetched data:', community);
      setCreateCommunityData(community);

    };
    fetchData();
  }, []);

return(
    <>
    <Header/>

    <Typography variant="h4" margin={3} color="hotpink"> 
        </Typography>
      <h1>Create Community</h1>
      <Grid container style={{ margin: 16, paddingBottom: 16, borderBottom: '1px solid pink' }}>    
        <Grid container style={{ display: 'flex', flexDirection: 'row' }}>
          <Grid container style={{ width: "33%", marginRight: 8 }}>
          </Grid>
          <Grid item style={{ width: "90%" }}>
            <h3>{community.length > 0 ? community[0].name : ''}</h3>
            {community.map((c: CommunityType) => (
              <p key={c._id}>{c.description}</p>
            ))}
        </Grid>
        </Grid>
        <Grid item style={{textAlign: 'center', marginBottom: 8}}>
            <strong>{community.map(c => c.price).join(', ')} Test-π</strong> <br />
          <Button variant='contained' color='secondary'sx={{ backgroundColor: 'pink' }} onClick={()=> {props.onClickBuy()}}>Join</Button>

        </Grid>
      </Grid>
    <h1>Communities</h1>
    {

      community.length === 0 ?
      <ProductCard
        name="None"
        description="No new communities"
        price={0}
        onClickBuy={() => console.log('Buy clicked')} // Pass the createCommunityData prop here
      />
      :
      community.map((order: CommunityType) =>{    
        <ProductCard
          name={order.name}
          description={order.description}
          price={order.price}
          onClickBuy={() => orderProduct("Community", order.price, { community_id: order._id })}
        />
      })
    }

       { showModal && <SignIn onSignIn={saveUser} onModalClose={onModalClose} showModal={showModal}/> }

    <MuiBottomNavigation/>
    </>
);

};
