//Created by Georgina Alacaraz
import React from "react";
import { UserContextType } from "./Types";
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import { Container } from "@mui/material";
import { UserContext } from "./Auth";

const logoImageUrl = 'df2.png'; // Replace with actual logo image URL

export default function Header() {
  const { user, saveUser } = React.useContext(UserContext) as UserContextType;
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);

  const handleMenu = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static" sx={{ backgroundColor: 'white', elevation: 0 }}>
        <Toolbar disableGutters>
          {/* Logo Image remains unchanged */}
          <Box sx={{ flexGrow: 1, display: 'flex', alignItems: 'center', overflow: 'hidden', height: '80px', width: '80px' }}>
            <img src={logoImageUrl} alt="Destig Femme" style={{ height: 'auto', width: '40%', objectFit: 'cover' }} />
          </Box>

          {/* IconButton for sign-in/sign-out, ensuring onClick is not redundantly set */}
          <IconButton
            size="large"
            aria-label="account of current user"
            onClick={saveUser} // Simplified onClick handler
            color="inherit"
          >
            {user.uid === '' ? (
              <Typography component="div" sx={{ color: 'black' }}>
                Sign-In
              </Typography>
            ) : (
              <Typography component="div" sx={{ color: 'black' }}>
                @{user.username} | Sign Out
              </Typography>
            )}
          </IconButton>
        </Toolbar>
      </AppBar>
    </Box>
  );

}
