// Date: 09/08/2021
// Description: This is the main page for the Add page. It will display the header, the form, and the bottom navigation bar.
// Created by Georgina Alacaraz

import React, { CSSProperties, useState } from 'react';
import axios from 'axios';
import { TextField, Button, Stack, colors, FormControl } from '@mui/material';
import { UserContext } from "../components/Auth";
import { UserContextType } from './Types';
import { userInfo } from 'os';

// Make TS accept the existence of our window.__ENV object - defined in index.html:
interface WindowWithEnv extends Window {
  __ENV?: {
    backendURL: string, // REACT_APP_BACKEND_URL environment variable
    sandbox: "true" | "false", // REACT_APP_SANDBOX_SDK environment variable - string, not boolean!
  }
}

const _window: WindowWithEnv = window;
const backendURL = _window.__ENV && _window.__ENV.backendURL;


const axiosClient = axios.create({ baseURL: `${backendURL}`, timeout: 20000, withCredentials: true});
const config = {headers: {'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*'}};


export default function MuiForm() {

    const [title, setTitle] = useState<string>('');
    const [description, setDescription] = useState<string>('');
    const [price, setPrice] = useState<string>('');

    const [titleError, setTitleError] = useState<boolean>(false);
    const [descriptionError, setDescriptionError] = useState<boolean>(false);
    const [priceError, setPriceError] = useState<boolean>(false);

    const [titleErrorMessage, setTitleErrorMessage] = useState<string>('');
    const [descriptionErrorMessage, setDescriptionErrorMessage] = useState<string>('');
    const [priceErrorMessage, setPriceErrorMessage] = useState<string>('');

    const { user, saveUser, showModal, saveShowModal, onModalClose } = React.useContext(UserContext) as UserContextType;


    const onTitleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setTitle(event.target.value);
    }

    const onDescriptionChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setDescription(event.target.value);
    }

    const onPriceChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setPrice(event.target.value);
    }

    const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();

        if (title === '') {
        setTitleError(true);
        setTitleErrorMessage('Title is required');
        } else {
        setTitleError(false);
        setTitleErrorMessage('');
        }

        if (description === '') {
        setDescriptionError(true);
        setDescriptionErrorMessage('Description is required');
        } else {
        setDescriptionError(false);
        setDescriptionErrorMessage('');
        }

        if (price === '') {
        setPriceError(true);
        setPriceErrorMessage('Price is required');
        } else {
        setPriceError(false);
        setPriceErrorMessage('');
        }


        if (title !== '' && description !== '' && price !== '') {
        const data = {
            title,
            description,
            price,
            user_id: user.uid
        };

        axiosClient.post('/community/create', data)
            .then((response) => {
            console.log(response);
            })
            .catch((error) => {
            console.log(error);
            });
        }
    }

    const modalStyle: CSSProperties = {
        background: 'white', 
        position: 'absolute', 
        left: '15vw', 
        top: '40%', 
        width: '70vw', 
        height: '25vh', 
        border: '1px solid pink', 
        textAlign: 'center', 
        display: 'flex', 
        flexDirection: 'column', 
        justifyContent: 'center' 
    }

    return (
        <Box
      component="form"
      sx={{
        '& > :not(style)': { m: 1 },
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
      }}
      noValidate
      autoComplete="off"
      onSubmit={handleSubmit}
    >
      <h2>Create your community here!</h2>
      <TextField
        label="Name of community"
        variant="outlined"
        value={communityName}
        onChange={(e) => setCommunityName(e.target.value)}
      />
      <TextField
        label="Description of Community"
        variant="outlined"
        multiline
        rows={4}
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <TextField
        label="Incentives"
        variant="outlined"
        value={incentives}
        onChange={(e) => setIncentives(e.target.value)}
      />
      <Button
        type="submit"
        variant="contained"
        sx={{ bgcolor: 'pink', ':hover': { bgcolor: 'pink', opacity: 0.8 } }}
      >
        Submit
      </Button>
    </Box>
  );
}
