//Created by Georgina Alacaraz

import { Button, Grid } from '@mui/material';
import { StringDecoder } from 'string_decoder';
import { UserContextType } from './Types';
import { UserContext } from './Auth';
import { useNavigate } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import { CommunityType } from './Types';


interface Props {
  name: string,
  description: string,
  // price: number,
  community: CommunityType,
}

export default function ProductCard(props: Props) {
  const navigate = useNavigate();



  // Define a function to handle the click and navigate to the chat page
  const handleNavigateToChat = (community: CommunityType) => {
    // get the community id and make it a current session
    console.log(community._id);
    navigate("/Chat", { state: { communityId: community._id } });
  };

  return (
    <Card style={{ margin: 16, paddingBottom: 10, marginLeft: 20, width: 'calc(100% - 40px)' }}>
      <CardContent>
        <Typography variant="h5" component="h2" gutterBottom style={{ fontWeight: 'bold' }}>
          {props.name}
        </Typography>
        <Typography variant="body1" component="p">
          {props.description}
        </Typography>
        {/* Example for price, uncomment if needed
        <Typography variant="body2" color="textSecondary">
          Price: {props.price}
        </Typography>
        */}
      </CardContent>
      <CardActions>
        <Button
          variant="contained"
          color="secondary"
          style={{ textTransform: 'none' }}
          onClick={() => handleNavigateToChat(props.community)} // Use the handleNavigateToChat function for the onClick event
        >
          Enter
        </Button>
      </CardActions>
    </Card>
  );
}
