// Import React and Material UI components as before...
import { Card, CardContent, CardActions, Typography, IconButton } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import { CommunityType } from './Types';
import { useLocation } from 'react-router-dom';
import HeartIcon from '@mui/icons-material/Favorite';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import { UserContext } from "./Auth";
import Grid from '@mui/material/Grid';


interface WindowWithEnv extends Window {
    __ENV?: {
      backendURL: string, // REACT_APP_BACKEND_URL environment variable
      sandbox: "true" | "false", // REACT_APP_SANDBOX_SDK environment variable - string, not boolean!
    }
}

const _window: WindowWithEnv = window;
const backendURL = _window.__ENV && _window.__ENV.backendURL;

const axiosClient = axios.create({ baseURL: `${backendURL}`, timeout: 20000, withCredentials: true });
const config = { headers: { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' } };


interface PostType {
    _id: string,
    title: string,
    description: string,
    comments: string[],
}

// Define the PostCard component    

export default function PostCard({ _id, title, description, comments }: PostType) {
    const navigate = useNavigate();
    const [isLiked, setIsLiked] = useState(false);
    const [likeCount, setLikesCount] = useState(0);



    const handleLike = async () => {
        try {
          // Optimistically update UI for better responsiveness
          setIsLiked(!isLiked);
          setLikesCount(isLiked ? likeCount - 1 : likeCount + 1);

          const response = await axiosClient.post(`/posts/like/${_id}`);
          // Update based on the actual response
          setIsLiked(response.data.isLiked);
          setLikesCount(response.data.likeCount);
        } catch (error) {
          console.error("Failed to handle post like/unlike: ", error);
          // Optionally revert optimistic updates in case of error
          setIsLiked(!isLiked);
          setLikesCount(isLiked ? likeCount + 1 : likeCount - 1);
        }
      };


    return (
        <Card style={{ margin: 16, paddingBottom: 10, marginLeft: 20, width: 'calc(100% - 40px)', boxShadow: "0 0 0 1px #E69BD1", backgroundColor: "#eec1e1"}}>
            <CardContent>
                <Grid container justifyContent="space-between" alignItems="center">
                    <Grid item>
                        <Typography variant="h6" gutterBottom style={{ fontWeight: 'bold' }}>
                            {props.title}
                        </Typography>
                    </Grid>
                </Grid>
                <Typography variant="body1" component="p">
                    {props.description}
                </Typography>
            </CardContent>


            <CardActions sx={{ marginTop: 'auto', display: 'flex', justifyContent: 'space-between' }}>
                  {/* Comment button */}
                  <div> {/* Wrap comment icon and button in a div */}
                    <IconButton aria-label="add a comment" onClick={() => navigate("/comments", { state: { postId: props._id } })}>
                      <ChatBubbleOutlineIcon style={{ fontSize: '16px', color: "black"}} />
                    </IconButton>
                    <Typography variant="body2" sx={{ color: "#9E4291", display: 'inline', marginLeft: '4px' }}>
                      {comments.length}
                    </Typography>


                  </div>
                  {/* Like button */}
                  <div> {/* Wrap like button and likes count in a div */}
                  <IconButton
                    aria-label="like post"
                    onClick={() => handleLike(props._id)}
                    style={{ padding: '0', marginRight: '4px' }}
                  >
                    <HeartIcon
                      style={{
                        fontSize: '16px',
                        fill: isLiked ? 'red' : 'none', // Change fill based on like status
                        stroke: isLiked ? 'red' : 'black', // Change stroke based on like status
                        strokeWidth: '2px',
                      }}

                    />
                  </IconButton>{likeCount}
                  </div>
                </CardActions>
        </Card>
    );

}
