// Created by Georgina Alacaraz
import MuiBottomNavigation from "../MuiBottomNavigation";
import Header from "./components/Header";
import { Box, Grid, Typography } from "@mui/material";
import { UserContextType, MyPaymentMetadata , CommunityType} from "./components/Types";
import { onCancel, onError, onReadyForServerApproval, onReadyForServerCompletion } from "./components/Payments";
import SignIn from "./components/SignIn";
import ProductCard from "./components/ProductCard";
import { UserContext } from "./components/Auth";
import React from "react";
import { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';


import axios from 'axios';
import { PanoramaSharp } from "@mui/icons-material";
// testing to link blog posts to blog pages

// Make TS accept the existence of our window.__ENV object - defined in index.html:
interface WindowWithEnv extends Window {
  __ENV?: {
    backendURL: string, // REACT_APP_BACKEND_URL environment variable
    sandbox: "true" | "false", // REACT_APP_SANDBOX_SDK environment variable - string, not boolean!
  }
}

interface CommunityCheckResponse {
  message: string;
  isCreator: boolean;
  isMember: boolean;
}

const _window: WindowWithEnv = window;
const backendURL = _window.__ENV && _window.__ENV.backendURL;


const axiosClient = axios.create({ baseURL: `${backendURL}`, timeout: 20000, withCredentials: true});
const config = {headers: {'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*'}};


export default function HomePage() {
  const { user, saveUser, showModal, saveShowModal, onModalClose } = React.useContext(UserContext) as UserContextType;
  const navigate = useNavigate(); // also added this!!!!!!
  const [createCommunityData, setCreateCommunityData] = useState<CommunityType[] | null>(null);

   // Add state to trigger effects
   const [triggerEffect, setTriggerEffect] = useState(false);

  const orderProduct = async (memo: string, amount: number, paymentMetadata: MyPaymentMetadata) => {
    if(user.uid === "") {
      return saveShowModal(true);

    }

    const paymentData = { amount, memo, metadata: paymentMetadata };
    const callbacks = {
      onReadyForServerApproval,
      onReadyForServerCompletion,
      onCancel,
      onError

    };

  const checkCommunityAndBuy = async (communityId: string, price: number, paymentMetadata: MyPaymentMetadata) => {
    console.log("checkCommunityAndBuy called", communityId, price, paymentMetadata);
    try {
      const communityCheckResponse = await axiosClient.get<CommunityCheckResponse>('/user/communityCheck', {
        params: {
          community_id: communityId,
          user_id: user.uid
        }
      });
      console.log(communityCheckResponse);

      if (communityCheckResponse.data.isMember) {
        navigate('/pages/joinedCommunity');
        console.log("You are a member of this community");
      } else if (communityCheckResponse.data.isCreator) {
        navigate('/pages/createdCommunity');
        console.log("You are the creator of this community");
      } else {
        orderProduct("Join Community", price, paymentMetadata);
        console.log("You have to pay to join this community");
    const payment = await window.Pi.createPayment(paymentData, callbacks);
    console.log(payment);
    // Make an API call to add person to the community if the payment was successful
    if (payment.paymentCompleted === true){
      console.log("Payment was successful");
    axiosClient.post('/user/addUser', paymentMetadata)
            //.then((response) => {
            //console.log(response);
            // navigate('/pages/chat'); // Redirect to the chat page!!!!!!!1
            //})
            .catch((error) => {
            console.log(error);
            });
         }
        }
      }
      catch (error) {
        console.log(error);
      }
  }

  useEffect(() => {
    console.log(createCommunityData);
  }, [createCommunityData]);

  useEffect(() => {
    // Make an API call to fetch the create community data
    axiosClient.get('/community/hi')
            .then((response) => {
            console.log(response);
            setCreateCommunityData(response.data);
            })
            .catch((error) => {
            console.log(error);
            });
        }

    , []);

return(
    <>

    <Header/>

    <Typography variant="h5" margin={2} color="#9E4291" style={{ fontWeight: 'bold' }}>
            Communities
        </Typography>


      { createCommunityData ?
      createCommunityData.map((order) =>{    
        console.log(order);
        return <ProductCard 
          name={order.name}
          description={order.description}
          price={order.price}
          onClickBuy={() => checkCommunityAndBuy(order._id, order.price, { community_id: order._id, user_id: user.uid })}
        />
      })
      :
      <ProductCard
        name="Loading.. ."
        description="Loading..."
        price={0}
        onClickBuy={() => console.log('Buy clicked')} // Pass the createCommunityData prop here
      />

 }

{ showModal && <SignIn onSignIn={saveUser} onModalClose={onModalClose} showModal={showModal}/> }

<MuiBottomNavigation/>
</>
);
}
}
