// Created by Georgina Alacaraz
import React, { useContext, useEffect, useState } from "react";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { UserContext } from "./Auth";
import { PostType, UserContextType } from "./Types";
import { set } from "mongoose";
import { Box, Grid, Typography, Card, CardContent } from '@mui/material';
import ButtonBase from '@mui/material/ButtonBase';
import { useLocation } from 'react-router-dom';
import { Link } from 'react-router-dom';
import CardActions from '@mui/material/CardActions';
import IconButton from '@mui/material/IconButton';
import CommentIcon from '@mui/icons-material/Comment';
import { PodcastsOutlined, PostAddOutlined } from "@mui/icons-material";

import HeartIcon from '@mui/icons-material/Favorite';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import { useFormState } from "react-hook-form";

interface WindowWithEnv extends Window {
  __ENV?: {
    backendURL: string, // REACT_APP_BACKEND_URL environment variable
    sandbox: "true" | "false", // REACT_APP_SANDBOX_SDK environment variable - string, not boolean!
  }
}

const _window: WindowWithEnv = window;
const backendURL = _window.__ENV && _window.__ENV.backendURL;

const axiosClient = axios.create({ baseURL: `${backendURL}`, timeout: 20000, withCredentials: true });
const config = { headers: { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' } };

export default function PostContent({ communityId }: { communityId: string }) {
  const { user, saveUser, showModal, saveShowModal, onModalClose } = useContext(UserContext) as UserContextType;
  const navigate = useNavigate();
  const [posts, setPosts] = useState<PostType[]>([]);
  const [isLiked, setIsLiked] = useState(false);
  const [likesCount, setLikesCount] = useState(0);

  const location = useLocation();
  const postId = location.state.postId;
  console.log(postId);

  // useEffect(() => {
  //   const fetchLikes = async () => {
  //     try {
  //       const response = await axiosClient.get(`/posts/likes/${postId}`);
  //       setLikesCount(response.data.likesCount);
  //       setIsLiked(response.data.isLiked);
  //     } catch (error) {
  //       console.error("Failed to fetch likes: ", error);
  //     }
  //     fetchLikes();
  //   }
  // }
  // , [postId]);

  const handleLike = async (postId: string) => {
    // Find the post index using the postId
    const postIndex = posts.findIndex(post => post._id === postId);
    if (postIndex !== -1) {
      try {
        const response = await axiosClient.post(`/posts/like/${postId}`);
        // Use functional update to ensure we're working with the most current state
        setPosts(currentPosts => {
          const newPosts = [...currentPosts];
          const newLikesCount = response.data.likeCount;
          const newIsLiked = response.data.isLiked;
          // Update the specific post with the new like status and count
          newPosts[postIndex] = {
            ...newPosts[postIndex],
            isLiked: newIsLiked,
            likesCount: newLikesCount
          };
          return newPosts;
        });
      } catch (error) {
        console.error("Failed to handle post like/unlike: ", error);
      }
    }
  };

  // get the posts that have the same community id as the current session
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axiosClient.get(`/posts/posts1?community_id=${communityId}`);
        const updatedPosts = response.data.posts.map(post => ({
          ...post,
          isLiked: false, // Initialize isLiked state
          likesCount: 0 // Initialize likesCount
        }));
        setPosts(updatedPosts);
      } catch (error) {
        console.error("Failed to fetch posts: ", error);
      }
    };
    fetchPosts();
  }, [communityId]);// Empty dependency array means this effect runs once on mount

  return (
    <Box sx={{ flexGrow: 1, margin: 2 }}>
      <Grid container spacing={2}>
        {Array.isArray(posts) && posts.length > 0 ? (
          posts.map((post, index) => (
            <Grid item xs={12} key={post._id}>
              <Card sx={{ flexDirection: 'column', minHeight: 100, width: "100%", backgroundColor:'#efc9e4' }}>
                <CardContent>
                  <Typography variant="h6" gutterBottom align="left">
                    {post.title}
                  </Typography>
                  <Typography variant="body1" color="text.secondary" align="left">
                    {post.description}
                  </Typography>
                  </CardContent>
                <CardActions sx={{ marginTop: 'auto', display: 'flex', justifyContent: 'space-between' }}>
                  {/* Comment button */}
                  <div> {/* Wrap comment icon and button in a div */}
                    <IconButton aria-label="add a comment" onClick={() => navigate("/comments", { state: { postId: post._id } })}>
                      <ChatBubbleOutlineIcon style={{ fontSize: '16px', color: "black"}} />
                    </IconButton>
                    <Typography variant="body2" sx={{ color: "#9E4291", display: 'inline', marginLeft: '4px' }}>
                      {post.comments.length}
                    </Typography>


                  </div>
                  {/* Like button */}
                  <div> {/* Wrap like button and likes count in a div */}
                  <IconButton
                    aria-label="like post"
                    onClick={() => handleLike(post._id)}
                    style={{ padding: '0', marginRight: '4px' }}
                  >
                    <HeartIcon
                      style={{
                        fontSize: '16px',
                        fill: isLiked ? 'red' : 'none', // Change fill based on like status
                        stroke: isLiked ? 'red' : 'black', // Change stroke based on like status
                        strokeWidth: '2px',
                      }}


                    />
                  </IconButton>{likesCount}
                  </div>
                </CardActions>
              </Card>
            </Grid>
          ))
        ) : (
          <Grid item xs={12}>
            <Typography variant="body1" textAlign="center">
              There are no posts in this community :(
            </Typography>
          </Grid>
        )}
      </Grid>
    </Box>
  );

}
