import { UserContextType, MyPaymentMetadata } from "../components/Types";
import { onCancel, onError, onReadyForServerApproval, onReadyForServerCompletion } from "../components/Payments";
import Header from "../components/Header";
import { UserContext } from "../components/Auth";
import React from "react";
import Typography from "@mui/material/Typography";
import Posts from "../components/posts";
import PostContent from "../components/PostContent";
import { useLocation } from 'react-router-dom';
import { withStyles } from '@mui/material/styles';
import SignIn from "../components/SignIn";
import { useState } from "react";
import { useEffect } from "react";


export default function ChatCreator() {
  const { user, saveUser, showModal, saveShowModal, onModalClose } = React.useContext(UserContext) as UserContextType;
  const [community, setCommunity] = useState(null);
  const location = useLocation();
  const communityId = location.state.communityId;
  console.log(communityId);


  const orderProduct = async (memo: string, amount: number, paymentMetadata: MyPaymentMetadata) => {
    if(user.uid === "") {
      return saveShowModal(true);
    }
    const paymentData = { amount, memo, metadata: { ...paymentMetadata, user_id: user.uid } };

    const callbacks = {
      onReadyForServerApproval,
      onReadyForServerCompletion,
      onCancel,
      onError
    };

    const payment = await window.Pi.createPayment(paymentData, callbacks);
    console.log(payment);
  }

  useEffect(() => {
    const fetchCommunities = async () => {
      try {
        const response = await fetch('/hi', {
          credentials: 'include', // Needed for sessions to work
        });
        if (response.ok) {
          const data = await response.json();
          setCommunity(data);
        } else {
          console.error('Failed to fetch communities');
        }
      } catch (error) {
        console.error('Error fetching communities:', error);
      }
    };
    fetchCommunities();
  }, [user]); 

return(
    <>
        <Header/>
        <Typography variant="h6" margin={2}  color="black" style={{ fontWeight: '' } }>
        {communities.length > 0 ? communities[0].name : 'No communities available'}
      </Typography>
        <Typography variant="h5" margin={2}  color="#9E4291" style={{ fontWeight: 'bold' } }>
        </Typography>
        <PostContent communityId={communityId}/>
        <Posts communityId={communityId} />

        { showModal && <SignIn onSignIn={saveUser} onModalClose={onModalClose} showModal={showModal}/> }
    </>
);

};
