//Created by Georgina Alacaraz

import { Button, Grid } from '@mui/material';
import { StringDecoder } from 'string_decoder';
import { UserContextType } from './Types';

/* DEVELOPER NOTE:
* the productCard is used to create the standard output of pies
* on the User to App payments page of the app.
*/

//for community page

interface Props {
  name: string,
  description: string,
  price: number,
  onClickBuy: () => void,
}

export default function ProductCard(props) {
  // State to hold the new comment's text
  const [newComment, setNewComment] = useState('');

  // Function to handle adding a new comment
  const handleAddComment = () => {
    // Assuming you have a method to add a comment to the array
    props.onAddComment({
      id: Date.now(), // Simple ID generation (you might want to use something more robust)
      author: "Anonymous", // This could be dynamic based on user's data
      text: newComment,
    });
    setNewComment(''); // Reset input after adding
  };

  return (
    <Grid container style={{ margin: 16, paddingBottom: 16, borderBottom: '1px solid pink' }}>
      <Grid container style={{ display: 'flex', flexDirection: 'row' }}>
        <Grid container style={{ width: "33%", marginRight: 8 }}>
          {/* Image or additional info could go here */}
        </Grid>
        <Grid item style={{ width: "90%" }}>
          <h3>{props.name}</h3> 
          <p>{props.description}</p>  
        </Grid>
      </Grid>
      <Grid item style={{textAlign: 'center', marginBottom: 8}}>
        <Button variant='contained' color='secondary' sx={{ backgroundColor: '#ffe6ff', color: "black" }} onClick={props.onClickBuy}>Join</Button>
      </Grid>
      {/* Comments Section */}
      <Grid item style={{ width: '100%' }}>
        <h4>Comments</h4>
        {props.comments && props.comments.map(comment => (
          <div key={comment.id}>
            <p><strong>{comment.author}</strong>: {comment.text}</p>
          </div>
        ))}
        <TextField 
          fullWidth 
          label="Add a comment" 
          variant="outlined" 
          value={newComment} 
          onChange={(e) => setNewComment(e.target.value)} 
          style={{ margin: '10px 0' }}
        />
        <Button variant="contained" color="primary" onClick={handleAddComment}>Post Comment</Button>
      </Grid>
    </Grid>
  );
}
