import React, { CSSProperties } from "react";
import { User } from "../";

interface Props {
  //onPosts:() => void;
  onSignIn: () => void;
  onSignOut: () => void;

  user: User | null
}

const headerStyle: CSSProperties = {
  padding: 8,
  backgroundColor: "white",
  color: "red",
  width: "100%",
  display: "flex",
  alignItems: "center",
  justifyContent: "space-between",
};

//add more headers on the top bars
export default function Header(props: Props) {
  return (
    <header style={headerStyle}>


      {/* <div> 
             <button type="button" onClick={onPosts}>News</button>
          </div>
      <div>
             <button type="button" onClick={onChat}>Quiz</button>
          </div>*/}

      <div>


        {props.user === null ? (
          <button style={{borderRadius: "30px"}} onClick={props.onSignIn}>Sign in</button>
        ) : (
          <div>
            @{props.user.username} <button type="button" onClick={props.onSignOut}>Sign out</button>
          </div>
        )}
      </div>

    </header>
  );

  function onPosts() {
    window.open("./public/quiz.html", "_blank");
  }
  function onChat() {
    window.open("./public/quiz.html", "_blank");
  }

}
