    const fetchComments = async () => {
      try {
        const response = await axiosClient.get(`/posts/posts1?_id=${postId}`);
        setComment(response.data.posts.comments || []);
      } catch (error) {
        console.error("Failed to fetch comments: ", error);
      }
    };
    fetchComments();
  }, [postId]);

  return (
    <div>
    {comments.map(comment => (
        <div key={comment._id.$oid}>
            <p>{comment.content}</p>
        </div>
    ))}
    </div>

  );
}
