// Created by Georgina Alacaraz
import { UserContextType, MyPaymentMetadata } from "../components/Types";
import { onCancel, onError, onReadyForServerApproval, onReadyForServerCompletion } from "../components/Payments";
import SignIn from "../components/SignIn";
import MuiBottomNavigation from "../../MuiBottomNavigation";
import Header from "../components/Header";
import ProductCard from "../components/ProductCard";
import { UserContext } from "../components/Auth";
import React, { useState, useEffect } from 'react';

/* DEVELOPER NOTE:
* this page facilitates the purchase of pies for pi. all of the callbacks
* can be found on the Payments.tsx file in components file. 
*/
export default function UserToAppPayments() {
  const { user, saveShowModal } = React.useContext(UserContext) as UserContextType;

  const orderProduct = async (memo: string, amount: number, paymentMetadata: MyPaymentMetadata) => {
    if(user.uid === "") {
      return saveShowModal(true);
    }

    const paymentData = { amount, memo, metadata: paymentMetadata };
    const callbacks = {
      onReadyForServerApproval,
      onReadyForServerCompletion,
      onCancel,
      onError
    };

    const payment = await window.Pi.createPayment(paymentData, callbacks);
    console.log(payment);
  };

  return (
    <div>
      {/* Other components or JSX can be included here */}
      <CommunitiesList orderProduct={orderProduct} />
    </div>
  );
}

// CommunitiesList Component
import axios from 'axios';

type OrderProductType = (memo: string, amount: number, paymentMetadata: { productId: string }) => void;

function CommunitiesList({ orderProduct }: { orderProduct: OrderProductType }) {
  const [communities, setCommunities] = useState<any[]>([]);

  useEffect(() => {
    axios.get('/community/create') // Adjust the endpoint as per your API
      .then(response => {
        setCommunities(response.data.communities);
      })
      .catch(error => {
        console.error('There was an error fetching the communities:', error);
      });
  }, []);

  return (
    <div>
      {communities.map(community => (
        <ProductCard
          key={community._id}
          name={community.name}
          description={community.description}
          price={community.price}
          pictureURL={community.pictureURL}
          pictureCaption={community.pictureCaption}
          onClickBuy={() => orderProduct("Join", community.price, { productId: community._id })}
        />
      ))}
    </div>

  );
}
<MuiBottomNavigation />
